sys_script_include.script:
function getFilteredRecordCount(table, encodedQuery) {
    var count = new GlideAggregate(table);
    count.addEncodedQuery(encodedQuery);
    count.addAggregate('COUNT');
    count.query();
    var recordCount = 0;
    if (count.next())
        recordCount = count.getAggregate('COUNT');
    return recordCount;

}
